    def cutfile(self):
        self.textEdit_2.cut()
        self.textEdit.cut()
        print(QApplication.clipboard().text())
        msg = QMessageBox(MainWindow)
        msg.setWindowTitle("Cut")
        msg.setText(QApplication.clipboard().text()+" Cut On Clipboard")
        msg.exec_()
    
    def pastefile(self):
        self.textEdit.setText(QApplication.clipboard().text())
        msg = QMessageBox(MainWindow)
        msg.setWindowTitle("Paste")
        msg.setText(QApplication.clipboard().text()+" Pasted Successfully")
